INSERT INTO `faith_community` (`community_id`, `registered_id`, `create_time`, `community_name`, `community_type`, `community_introduce`, `community_reason`, `community_status`) VALUES
(1, 14210324, 1493519267, '社团联合会', '文化体育类', '学生社团联合会是在校团委的直接领导下，管理社团日常工作，服务社团发展的学生组织。管辖着包涵文学学术、实践服务、艺术文化、爱好兴趣、体育运动的功能丰富类型兼备的学生社团组织。目前社联主席团以下下设八个部门。', NULL, 1),
(2, 16530219, 1493651778, '英语协会', '文化体育类', 'The Voice Of Dreams\r\n英语协会是成立于2012年10月4日的社联领导下的非盈利性的综合性社团。', NULL, 1),
(3, 16530218, 1493655591, '4+1街舞社', '文化体育类', '4+1街舞社\r\n大多数人想要改变这个世界，却罕有人想改造自己。', NULL, 1),
(4, 14210315, 1493655925, '流火轮滑社', '文化体育类', '流火轮滑社是2012年经学校批准成立的直排轮爱好者自行发起和组织的校园社团。', NULL, 1),
(5, 16240135, 1493656682, '时光魅影摄影团', '文化体育类', ' 南邮通达时光魅影摄影团成立于2012年，是一个集文艺、公益、娱乐性社团，宗旨在于弘扬摄影文化、提升学生素养。', NULL, 1),
(6, 15410206, 1493656786, '起灵舞蹈社', '文化体育类', '起灵舞蹈社以团结、刻苦、自信、美丽为社团宗旨，长期开展舞蹈类活动，有古典舞、现代舞、健美操、拉丁、恰恰等舞种。', NULL, 1),
(7, 16110216, 1493656897, '耽悦表演社', '文化体育类', ' 无表演 不精彩 无耽悦 不欢悦，本社团由学生自发组织，活动健康向上，有益于同学们的身心发展，丰富校园的文化生活，属于非营利性学生社会实践组织。\r\n', NULL, 1),
(8, 16530221, 1493657076, '疾影漂移社团', '文化体育类', ' 疾影漂移社立志于为滑板运动爱好者提供一个互相学习、互相交流的平台，上帝没有给我们翅膀，却让轮子带我们飞翔。\r\n', NULL, 1),
(9, 16520114, 1493658363, '吉他社', '文化体育类', '吉他社是一个有特色、有活力、能激发你的音乐细胞细胞、动荡你的心灵音符的组织。', NULL, 1),
(10, 16310206, 1493657856, '裁判社', '文化体育类', '裁判社是一个以篮球的裁判项目为主的社团，其中包括裁判人员的训练以及各类各种篮球比赛的裁判任务', NULL, 1),
(11, 16520324, 1493658566, '读书协会', '文化体育类', '读书协会以提升校园人文素质为己任，激发同学们的读书兴趣，传播文化，传播思想，丰富大学生活，充实精神世界，共建良好的读书氛围，激发思想的碰撞。', NULL, 1),
(12, 16530115, 1493658785, '慢跑社', '文化体育类', '   跑步社是由热爱运动，崇尚健康，有着顽强毅力的学子为基础而组成的社团组织。', NULL, 1),
(13, 14210316, 1493870728, '少帅', '学术科技类', '1234                       ', '124125412                                       ', -1);

